mapper.update:
UPDATE options
SET o_content = #{content}
WHERE o_id = #{optionId}

mapper.select:
SELECT o_id optionId,o_content content,o_iscorrect iscorrect,o_analysis analysis
FROM options
WHERE o_question_id = #{id};

SELECT o_id optionId,o_content content,o_iscorrect iscorrect,o_analysis analysis
FROM options
WHERE o_id = #{optionId};
